function inscription_achat()
{
    var nom_m = $("#nom_m").val();
    var prenom_m = $("#prenom_m").val();
    var password_m = $("#password_m").val();
    var password_m1 = $("#password_m1").val();
    var telephone_m = $("#telephone_m").val();
    var telephone_m1 = $("#telephone_m1").val();
    $.ajax({
            url:base_url+'inscription/s_inscrire_achat', 
            method:'POST', 
            data:{nom_m:nom_m,prenom_m:prenom_m,password_m:password_m, password_m1:password_m1, telephone_m:telephone_m, telephone_m1:telephone_m1}, 
            success:function(result){
                if (result > 0) {
                    $("#id_acheteur").val(result);
    
                   $("#form_inscription").submit();
                }
                else
                {
                    $("output").html(result);
                }
                
                }
            });
}

function connexion_achat()
{
    var password_m  = $("#password_m2").val();
    var telephone_m = $("#telephone_m2").val();
    $.ajax({
            url:base_url+'connexion/se_connecter_achat', 
            method:'POST', 
            data:{password_m:password_m,telephone_m:telephone_m}, 
            success:function(result){
                if (result > 0) {
                    $("#id_acheteur").val(result);
                   
                    $("#form_inscription").submit();
                }
                else
                {
                    $("output").html(result);
                }
            }
    });
}
/*admin*/
function produit_marquer_recu(id_vente)
{
    if (confirm('Marquer ce produit reçu par le client ?')) {
        $("#btn_action_"+id_vente).html('Traitement ...');
        $.ajax({
                url:base_url+'admin/produit_marquer_recu', 
                method:'POST', 
                data:{id_vente:id_vente}, 
                success:function(result){
                    if (result > 0) {
                        $("#btn_action_"+id_vente).fadeOut();
                        $("#commande_reception_"+id_vente).toggleClass('ok');
                    }
                    else
                    {
                        $("#btn_action_"+id_vente).html('Error');
                    }
                }
        });
    }
}
