function produit_marquer_payer(id_vente)
{
    if (confirm('Marquer cette commande comme payée ?')) {
        $("#btn_action_"+id_vente).html('Traitement ...');
        $.ajax({
                url:base_url+'admin/produit_marquer_payer', 
                method:'POST', 
                data:{id_vente:id_vente}, 
                success:function(result){
                    if (result > 0) {
                        $("#btn_action_"+id_vente).fadeOut();
                        $("#commande_paiement_"+id_vente).toggleClass("ok");
                        alert('Mail envoyé au vendeur');
                    }
                    else
                    {
                        $("#btn_action_"+id_vente).html('Error');
                    }
                }
        });
    }
}

