function search(id_agence)
{
    var etat = $("#etat").val();
    var key = $("#key").val();
    var cat = $("#categorie").val();
    document.location = base_url+'article/search?key='+key+'&localisation='+id_agence+'&etat='+etat+'&cat='+cat;
}