$("#btn-submit-insc").on( "click", function(event) { 
    event.preventDefault();

    $(".form_accueil").css('border-color', '#ccc');
    var nom_m       = $("#nom_m").val();
    var prenom_m    = $("#prenom_m").val();
    var login_m     = $("#login_m").val();
    var telephone_m = $("#telephone_m").val();
    var email_m     = $("#email_m").val();
    var password_m1 = $("#password_m1").val();
    var password_m  = $("#password_m").val();

    if ( (nom_m == "")) {
        $("#nom_m").css('border-color', '#da0202');
        $("#output").html('Vérifier votre nom');
    }
    else if ( (prenom_m == "")) {
        $("#prenom_m").css('border-color', '#da0202');
        $("#output").html('Vérifier votre prénom');
    }
    else if ( (login_m == "")) {
        $("#login_m").css('border-color', '#da0202');
        $("#output").html('Vérifier votre pseudo');
    }
    else if ( (email_m == "")) {
        $("#email_m").css('border-color', '#da0202');
        $("#output").html('Vérifier votre adresse e-mail');
    }
    else if ( (password_m1 == "")) {
        $("#password_m1").css('border-color', '#da0202');
        $("#output").html('Vérifier votre mot de passe');
    }
     else if ( (password_m == "")) {
        $("#password_m").css('border-color', '#da0202');
        $("#output").html('Vérifier votre mot de passe');
    }
    else if ( (password_m != password_m1)) {
        $("#password_m").css('border-color', '#da0202');
        $("#output").html('Les mots de passe ne sont pas identiques');
    }
    else
    {
        $("#form_inscription").submit();
    }
});