function details(id_package)
{
    $.ajax({
            url:base_url+'package/details', 
            method:'POST', 
            data:{id_package:id_package}, 
            success:function(result){
                $("#place_modal_description").html(result);
                }
            });
}
function ajouterpanier(nombre_achat, id_package)
{
//alert(nombre_achat);
    if (nombre_achat > 0) {
        stock = $("#stock").val();
        if (stock >= nombre_achat) {
            $.ajax({
                    url:base_url+'produit/ajouterpanier', 
                    method:'POST', 
                    data:{id_package:id_package, nombre_achat:nombre_achat}, 
                    success:function(result){
                            if (result != "0") {
                                $(".count_panier").html(result);
                                document.location = base_url+"article/panier";
                            }
                            else
                            {
                                alert('Vous ne pouvez pas acheter vos produits !');
                            }
                        }
                    });
        }
        else alert('Il n\'y a que '+stock+' dans le stock');
    }
   // else prompt('Firy amin\'io no hovidinao ?', 'Azafady Tompoko :',ajouterpanier,id_package)

}
function removepanier(key)
{
    $.ajax({
            url:base_url+'produit/removepanier', 
            method:'POST', 
            data:{key:key}, 
            success:function(result){

                $("#droite").html(result);
                }
            });
}