$("#btn-submit").on( "click", function(event) { 
    event.preventDefault();
    $("#number1").css('border-color', '#ccc');
    $("#number2").css('border-color', '#ccc');
    $("#number1").css('border-color', '#ccc');

    var number1 = $("#number1").val();
    var number2 = $("#number2").val();
    var password = $("#password").val();

    if ( (number1 == "") ||( number2 == "") || (number1 != number2) ) {
        $("#number1").css('border-color', '#da0202');
        $("#number2").css('border-color', '#da0202');
        $("#output").html('Vérifier vos numeros de téléphone');
    }
    else if( password == "")
    {
         $("#number1").css('border-color', '#da0202');
         $("#output").html('Vérifier votre mot de passe');
    }
    else
    {
        $("#form_demande").submit();
    }
});

