var chercher_user_produit = function()
{
    $("#result_recherche").html(attente);
    var user_srch = $("#user_srch").val();
    var produit_srch = $("#produit_srch").val();
    $.ajax({
    url:base_url+'validation/search_user_produit', 
    method:'POST', 
    data:{user_srch:user_srch, produit_srch:produit_srch}, 
    success:function(result){
        $("#result_recherche").html(result);
        }
    });
}
