var active = function(id_package){
      var active = 1;
      if ($("#ouvert_"+id_package).is(':checked')) {
        active = 0;
      }
      $.ajax({
        url:base_url+'validation/activation', 
        method:'POST', 
        data:{active:active,id_package:id_package}, 
        success:function(result){
             console.log('Active/desact ok');
                } 
            });
    }
